import React, { Component } from 'react';
import './App.css';

class App extends Component {

  state = {
    monsters: [
      {
        name: "Frankenstein",

      },
      {
        name: "Dracula",

      },
      {
        name: "Zombie",

      }
    ]

  }
  render(){

    return (
      <div>
        {
          this.state.monsters.map((monster)=> <h1>monster.name</h1>)
        }
      </div>
    )
  }

}

export default App;
