import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(){
    super();

    this.state = {
      monsters: [
        {
          name: "Frankenstein",
          id: 0

        },
        {
          name: "Dracula",
          id: 1

        },
        {
          name: "Zombie",
          id: 2

        }
      ]
    }
  }

  render(){

    return (
      <div>
        {
          this.state.monsters.map((monster)=> <h1 key={this.monster.id}>{monster.name}</h1>)
        }
      </div>
    )
  }

}

export default App;
